INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C25157','Body Surface Area',null,'Body Surface Area','A measure of the 2-dimensional extent of the body surface (i.e., the skin).  Body surface area (BSA) can be calculated by mathematical formula or from a chart that relates height to weight.  BSA is often an important factor in dosing.');
