INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C25301','Day',null,'Day','The time for Earth to make a complete rotation on its axis; ordinarily divided into twenty-four hours, equal to 86 400 seconds. This also refers to a specific day.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C42559','Degree Celsius',null,'Degree Celsius','A unit of temperature of the temperature scale designed so that the freezing point of water is 0 degrees and the boiling point is 100 degrees at standard atmospheric pressure. The current official definition of the Celsius sets 0.01 C to be at the triple point of water and a degree to be 1/273.16 of the difference in temperature between the triple point of water and absolute zero. One degree Celsius represents the same temperature difference as one Kelvin.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C44277','Degree Fahrenheit',null,'Degree Fahrenheit','A unit of temperature of the temperature scale designed so that the freezing point of water is 32 degrees and the boiling point is 212 degrees, placing the boiling and melting points of water 180 degrees apart. One degree Fahrenheit is 5/9ths of a kelvin (or of a degree Celsius), and minus 40 degrees Fahrenheit is equal to minus 40 degrees Celsius.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C25299','Diastolic Blood Pressure',null,'Diastolic Blood Pressure','The minimum pressure exerted into the systemic arterial circulation during cardiac ventricular relaxation and filling.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C48155','Gram',null,'Gram','The metric unit of mass equal to one thousandth of a kilogram. One gram equals approximately 15.432 grains or 0.035 273 966 ounce.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C49677','Heart Rate',null,'Heart Rate','The number of heartbeats per unit of time, usually expressed as beats per minute.');
