INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C28252','Kilogram',null,'Kilogram','A basic SI unit of mass. It is defined as the mass of an international prototype in the form of a platinum-iridium cylinder kept at Sevres in France. A kilogram is equal to 1,000 grams and 2.204 622 6 pounds.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C49679','Mean Arterial Pressure',null,'Mean Arterial Pressure','The mean pressure of the blood within the arterial circulation. The arterial pressure may be directly measured by insertion of an intra-arterial catheter connected to a transducer. The mean arterial pressure (MAP) can be calculated by subsequent analysis of the waveform. MAP can be approximated without an invasive procedure using the following formula: diastolic pressure plus 1/3 of the pulse pressure, where pulse pressure is systolic pressure - diastolic pressure.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C49670','Millimeter of Mercury',null,'Millimeter of Mercury','A non-SI unit of pressure equal to 133,332 Pa or 1.316E10-3 standard atmosphere. Use of this unit is generally deprecated by ISO and IUPAC.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C29846','Month',null,'Month','One of the 12 divisions of a year as determined by a calendar. It corresponds to the unit of time of approximately to one cycle of the moon''s phases, about 30 days or 4 weeks.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C25613','Percentage',null,'Percentage','A fraction or ratio with 100 understood as the denominator.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C48531','Pound',null,'Pound','The traditional unit of mass. By international agreement, one avoirdupois pound is equal to exactly 0.453 592 37 kilogram, 16 ounces, or 1.215 28 troy pounds.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C49676','Pulse Rate',null,'Pulse Rate','The rate of the pulse as observed in an artery, expressed as beats per minute. It can be measured at several anatomical sites, including the wrist, neck, temple, groin, behind the knees, or on top of the foot.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C49678','Respiratory Rate',null,'Respiratory Rate','The rate of breathing (inhalation and exhalation) measured within in a unit time, usually expressed as breaths per minute.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C87054','Sagittal Abdominal Diameter',null,'Sagittal Abdominal Diameter','A standard measure of visceral obesity, or abdominal fat, which is measured from the patient''s back to upper abdomen between the bottom of the rib cage and the top of the pelvic area. This measurement may be taken with the patient standing or in the supine position.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C42569','Square Meter',null,'Square Meter','A SI unit of area measurement equal to a square whose sides are one meter long. Square meter is equal to 10,000 square centimeters; 0.01 are; 1.196 square yards; 10.76 square feet; 1550 square inches.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C25298','Systolic Blood Pressure',null,'Systolic Blood Pressure','The maximum pressure exerted into the systemic arterial circulation during the contraction of the left ventricle of the heart.');
INSERT INTO ERX.NCPDP_CODE (NCIT_SUBSET_CODE,NCPDP_SUBSET_PREF_TERM,NCIT_CODE,NCPDP_PREF_TERM,NCPDP_SYNONYM,NCIT_PREF_TERM,NCIT_DEFINITION) VALUES ('C91101','NCPDP MeasurementUnitCode Terminology','C25206','Temperature',null,'Temperature','A measure of the average kinetic energy of a system of particles. Temperature may be quantified, in the context of thermodynamics, as the potential of one system to transfer thermal energy to another system until both systems reach a state of thermal equilibrium.');
